import React from "react";
import { fetchGVR } from "./api";
import {
  Text,
  StyleSheet,
  View,
  TouchableOpacity,
  Linking,
} from "react-native";

const HomeScreen = () => {
  const makeCall = () => {
    fetchGVR();
    // Linking.openURL(`tel:${9044957480}`);
  };

  const sendEmail = () => {
    Linking.openURL(`mailto:guardianconnect@guardianfueltech.com`);
  };
  return (
    <View>
      <Text style={styles.text}>Hello!</Text>
      <TouchableOpacity onPress={() => makeCall()}>
        <Text style={styles.text}>Call Guardian Connect</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => sendEmail()}>
        <Text style={styles.text}>E-Mail Guardian Connect</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30,
    textAlign: "center",
    marginTop: 20,
  },
});

export default HomeScreen;
