const BASE_URL = "https://reactgvr.herokuapp.com/api/gvrs";

export async function fetchGVR(id) {
  try {
    const { data } = await axios.get(`${BASE_URL}/${id}`);
    let Id = data.gvr;
    console.log(Id);
    return data;
  } catch (error) {
    throw error;
  }
}
